;|| FUNCOES ||
func:
    ; seta o modo de video
    setVid:
        mov ah, 00h
        mov al, 13h
        int 10h
    ret

    ; printa o caractere recebido
    putchar:
        mov ah, 0x0e
        mov bl, 15
        int 10h
    ret

    ; printa o caractere recebido de uma cor especifica
    ; amarelo
    putcharam:
        mov ah, 0x0e
        mov bl, 0xe
        int 10h
    ret 

    ; azul
    putcharaz:
        mov ah, 0x0e
        mov bl, 0x9
        int 10h
    ret

    ; verde
    putcharvd:
        mov ah, 0x0e
        mov bl, 0x2
        int 10h
    ret

    ; vermelho
    putcharvm:
        mov ah, 0x0e
        mov bl, 0x4
        int 10h
    ret

    ; magenta
    putcharmg:
        mov ah, 0x0e
        mov bl, 0x5
        int 10h
    ret

    ; printa uma string
    printstring:             
        .loop:
            lodsb           ; bota character em al 
            cmp al, 0       ; ve se a string acabou
            je .endloop     ; se acabou, retorna
            call putchar    ; se não, printa
            jmp .loop       ; repete o loop
        .endloop:
            ret

    ; printa o quadrado do menu incial
    printquada:             
        .loop:
            lodsb           ; bota character em al 
            cmp al, 17      ; ve se a string acabou (ela acaba com o numero 17)
            je .endloop     ; se acabou, retorna
            call putchar    ; se não, printa
            jmp .loop       ; repete o loop
        .endloop:
            ret

    ; printa uma string colorida
    printstringcor:
        .loop:
            lodsb           ; bota character em al 
            cmp al, 0       ; ve se a string acabou
            je .endloop     ; se acabou, retorna
            ; faz a comparacao de bx para saber a cor
            cmp bx, 1
            je .am
            cmp bx, 2
            je .az
            cmp bx, 3
            je .vd
            cmp bx, 4
            je .vm

            call putchar    ; se não achar, printa branco
            jmp .loop       ; repete o loop

            ; se achar printa com a cor especifica e repete o valor de bx
            ; amarelo
            .am:
                call putcharam
                mov bx, 1
                jmp .loop
            ; azul
            .az:
                call putcharaz
                mov bx, 2
                jmp .loop
            ; verde
            .vd:
                call putcharvd
                mov bx, 3
                jmp .loop
            ; vermelho
            .vm:
                call putcharvm
                mov bx, 4
                jmp .loop
        .endloop:
            ret

    ; lê um caractere e retorna
    getchar:
        mov ah, 0x00
        int 16h
    ret

    ; quebra uma linha
    endl:
        mov al, 0x0a            ; coloca o caractere de final de linha em al
        call putchar            ; imprime o final de linha
        mov al, 0x0d            ; coloca o cursor na próxima linha
        call putchar            ; imprime
    ret

    ; deleta um caractere
    delchar:
        mov al, 0x08            ; coloca o backspace em al
        call putchar            ; printa o backspace
        mov al, ' '             ; coloca um caractere em branco em al
        call putchar            ; printa o caractere em branco
        mov al, 0x08            ; coloca o backspace em al
        call putchar            ; printa o backspace
    ret

    ; lê uma string
    getstring:
        xor cx, cx              ; iguala o contador do tamanho da string a 0     

        loop:
            call getchar        ; lê o caractere
            cmp al, 0x08      
            je apagar           ; se o caractere for o backspace pula pra funcão de apagar
            cmp al, 0x0d      
            je enter            ; se o caractere for o enter pula pra conclusão
            cmp cl, 6           ; seta o tamanho máximo da palavra
            je loop                     
                
            stosb               ; guarda o caractere
            inc cl              ; incrementa o contador

            ; verefica se o valor para saber que cor printar o numero
            cmp al, "1"
            je pam
            cmp al, "2"
            je paz
            cmp al, "3"
            je pvd
            cmp al, "4"
            je pvm
                                
            call putcharmg       ; se nao achar, printa na tela o caractere que você digitou em magenta
                
            jmp loop             ; repete o loop

            ; se achar printa na respectiva cor
            ; amarelo
            pam:
                call putcharam
                jmp loop
            ; azul
            paz:
                call putcharaz
                jmp loop
            ; verde
            pvd:
                call putcharvd
                jmp loop
            ; vermelho
            pvm:
                call putcharvm
                jmp loop

            ; para quando apertar o backspace
            apagar:
                cmp cl, 0       ; verifica se a palavra esta limpa
                je loop         ; se tiver volta para o loop da escrita
                dec di          ; decrementa di e o contrador
                dec cl
                mov byte[di], 0 ; coloca 0 no valor do byte de di
                call delchar    ; chama o apaga caracter
                jmp loop      ; volta para o loop da escrita
        
        ; para quando apertar o enter
        enter:
            mov al, 0           ; seta al como 0
            stosb               ; guarda na memória
            call endl           ; chama o quebra linha
            ret
    
    ; transforma uma string em inteiro
    stoi:
        xor cx, cx              ; zera o ax e o cx
        xor ax, ax

        .loop1:
            push ax             ; da um push em ax
            lodsb               ; carrega o caractere da memoria
            mov cl, al          ; salva o caractere em cl
            pop ax              ; da um pop em ax
            cmp cl, 0           ; vê se chegou no fim da string
            je .endloop1
            sub cl, 48          ; tira o '0' da palavra salva em cl
            mov bx, 2           ; coloca 2 em bx
            mul bx              ; multiplica ax por bx
            add ax, cx          ; soma o resultado com cx
            jmp .loop1
        .endloop1:
            ret 
        
    ; inverte uma string
    reversestring:              
        mov di, si          ; coloca o endereço de si em di
        xor cx, cx          ; zera o contador

        ; carrega a palavra na pilha
        .loop1:             
            lodsb           ; carrega um caracter em al da memória
            cmp al, 0       ; vê se al vale 0
            je .endloop1    ; se for, a palavra acabou e finaliza o loop1
            inc cl          ; se não for, incrementa o contador
            push ax         ; da um push em ax na pilha
            jmp .loop1      ; repete o loop
        .endloop1:

        ; descarrega a palavra da pilha (invertida)
        .loop2:
            cmp cl, 0       ; ve se o contador vale 0
            je .endloop2    ; se valer, a palavra acabou, encerra a funcão
            dec cl          ; se não, decrementa o contador               
            pop ax          ; da um pop em ax da pilha
            stosb           ; guarda na memória
            jmp .loop2      ; repete o loop
        .endloop2:
            ret

    ; transforma um inteiro em uma string
    tostring:              
        push di                 ; da um push no endereço de di

        .loop1:
            cmp ax, 0           ; vê se ax chegou no final
            je .endloop1        ; se chegou vai para o encerramento do loop
            xor dx, dx          ; zera dx
            mov bx, 10          ; coloca 10 em bx
            div bx              ; divide ax por bx
            xchg ax, dx         ; troca ax por dx
            add ax, 48          ; coloca um '0' em ax para sinalizar final de string
            stosb               ; guarda na memória
            xchg ax, dx         ; troca ax por dx
            jmp .loop1          ; volta pro loop

        ;encerramento do loop
        .endloop1:
            pop si              ; da um pop no endereço de si     
            cmp si, di          ; vê se si e di são iguais
            jne .done           ; se não são, pula pro final direto
            mov al, 48          ; se são, coloca um '0' para sinalizar final de string
            stosb               ; guarda na memória 
        .done:
            mov al, 0           ; coloca 0 em al
            stosb               ; guarda na memoria
            call reversestring  ;inverte a string
        ret

    ; func para printar imagem apagada
    printimgap:
        mov dx, 10                  ; coloca a posicao inicial da linha           
        mov cx, 120                 ; coloca a posicao inicial da coluna

        ; loop da linha
        .linha:
            cmp dx, 90              ; verifica se chegou na linha final
            je .fimlinha            ; se chegou, vai para o final do loop da linha 
            mov cx, 120             ; se não, vai para o loop da coluna

            ; loop da coluna
            .coluna:
                cmp cx, 200         ; verifica se chegou na coluna final
                je .fimcoluna       ; se chegou, vai para o final do loop da coluna
                lodsb               ; carrega o pixel em al
                cmp al, 0           ; ve se o pixel e preto
                jne .print          ; se nao for printa o pixel
                jmp .incr           ; se for, incrementa a coluna
            .fimcoluna:
                inc dx              ; incrementa dx
                jmp .linha          ; volta pro loop da linha
            
        ; finaliza a impressao       
        .fimlinha:
            ret

        ; incrementa a coluna para pular caracteres pretos da imagem
        .incr:
            inc cx              
            jmp .coluna

        ; faz a impressao do pixel
        .print:
            mov ah, 0ch             ; seta o ah para escrever um pixel
            int 10h                 ; printa
            inc cx                  ; incrementa cx
            jmp .coluna             ; volta pro loop da coluna


    ; func para printar imagem colorida
    printimg:
        mov dx, 30                 ; coloca a posicao inicial da linha
        mov cx, 120                ; coloca a posicao inicial da coluna

        ; loop da linha
        linha:
            cmp dx, 110            ; verifica se chegou na linha final
            je fimlinha            ; se chegou, vai para o final do loop da linha 
            mov cx, 120            ; se não, vai para o loop da coluna

            ; loop da coluna
            coluna:
                cmp cx, 200        ; verifica se chegou na coluna final
                je fimcoluna       ; se chegou, vai para o final do loop da coluna
                lodsb              ; carrega a cor em al
                cmp al, 0          ; ve se a cor eh preto
                jne comp           ; se nao for vai comparar
                jmp incr           ; se for, incrementa a coluna
            fimcoluna:
                inc dx             ; incrementa dx
                jmp linha          ; volta pro loop da linha
            
        ; finaliza a impressao       
        fimlinha:
            ret

        ; incrementa a coluna para pular pixels pretos
        incr:
            inc cx
            jmp coluna

        ; printa o pixel
        print:
            mov ah, 0ch             ; seta o ah para escrever um pixel
            int 10h                 ; printa
            inc cx                  ; incrementa cx
            jmp coluna              ; volta pro loop da coluna

        ; compara se o pixel e de uma cor especifica e pula para ela
        comp:
            cmp byte[di], 1
            je amarelo
            cmp byte[di], 2
            je azul
            cmp byte[di], 3
            je verde
            cmp byte[di], 4
            je vermelho

            ; se nao for printa
            jmp print

        ; acende em amarelo
        amarelo:
            cmp al, 6
            jne print
            mov al, 14
            mov ah, 0ch             ; seta o ah para escrever um pixel
            int 10h                 ; printa
            inc cx                  ; incrementa cx
            jmp coluna              ; volta pro loop da coluna

        ; acende em azul
        azul:
            cmp al, 1
            jne print
            mov al, 9
            mov ah, 0ch        ; seta o ah para escrever um pixel
            int 10h            ; printa
            inc cx             ; incrementa cx
            jmp coluna         ; volta pro loop da coluna

        ; acende em verde
        verde:
            cmp al, 2
            jne print
            mov al, 10
            mov ah, 0ch        ; seta o ah para escrever um pixel
            int 10h            ; printa
            inc cx             ; incrementa cx
            jmp coluna         ; volta pro loop da coluna    

        ; acende em vermelho
        vermelho:
            cmp al, 4
            jne print
            mov al, 12
            mov ah, 0ch        ; seta o ah para escrever um pixel
            int 10h            ; printa
            inc cx             ; incrementa cx
            jmp coluna         ; volta pro loop da coluna  

    ; limpa as coisas digitadas e mostradas anteriormente
    limpatela:
        ; coloca o cursor no topo da tela                   
        mov dx, 0 
        mov bh, 0      
        mov ah, 0x2
        int 0x10

        ; printa 1000 caracteres em branco pra limpar
        mov cx, 1000 
        mov bh, 0
        mov al, 0x20
        mov ah, 0x9
        int 0x10
        
        ; reseta o cursor pro topo da tela
        mov dx, 0 
        mov bh, 0      
        mov ah, 0x2
        int 0x10
        ret 

    ; pausa o programa por 1 segundo
    delay:           
        mov cx, 0fh
        mov dx, 4240h
        mov ah, 86h
        int 15h
    ret

    ; copia uma string (ou sequencia) para outra
    ; coloca si na string de origem e di na string de destino
    strcpy:
    .loop1:
        lodsb                       ; carrega de si
        stosb                       ; guarda em di
        cmp al, 0                   ; ve se chegou ao final
        je .endloop1                ; se chegou encerra o loop
        jmp .loop1                  ; se nao, repete
    .endloop1:
    ret

    ; escolhe um numero aleatoriamente dentro de um intervalo usando o system time
    random: 
        mov ah, 00h                 ; interrupcao para pegar o system time        
        int 1ah                     ; cx e dx agora guardao o numero do system time     

        mov ax, dx                  ; guarda o valor de dx em ax   
        xor dx, dx                  ; zera dx
        mov cx, 5                   ; define o limete do intervalo, numeros de 0 ~ 5
        div cx                      ; divide ax por 5

        add dl, '0'                 ; coloca '0' no final de al
        mov al, dl

        ; compara se o valor do numero obtido para escolher a sequencia de cores correspondente
        cmp al, '1'
        je seq1

        cmp al, '2'
        je seq2

        cmp al, '3'
        je seq3

        cmp al, '4'
        je seq4

        cmp al, '5'
        je seq5

        ; faz a copia da sequencia escolhida aleatoriamente para a sequencia que sera usada
        seq1:
            mov si, sequencia1
            mov di, sequencia
            call strcpy

            ret
        seq2:
            mov si, sequencia2
            mov di, sequencia
            call strcpy

            ret
        seq3:
            mov si, sequencia3
            mov di, sequencia
            call strcpy

            ret
        seq4:
            mov si, sequencia4
            mov di, sequencia
            call strcpy

            ret
        seq5:
            mov si, sequencia5
            mov di, sequencia
            call strcpy

            ret

        ; se ouver algum resultado indesejado, roda a funcao novamente
        jmp random
    ret  

;|| MACROS ||
macros:

    ; macro para o menu
    %macro menu 0

        ; coloca em modo de video
        call setVid
        call limpatela
    
        loop..:
            mov si, quada
            call printquada

            mov si, apagado
            call printimgap

            setcursor 11, 15
            mov si, nome
            call printstring

            setcursor 18, 6
            mov si, play
            call printstring

            setcursor 18, 24
            mov si, cred
            call printstring

            setcursor 21, 15
            mov si, sair
            call printstring

            ; interrupcao para esperar o usuario digitar algo
            mov ah, 00h
            int 16h

            cmp al, "1"
            je .play

            cmp al, "2"
            je .cred

            cmp al, "3"
            je .fim

            ; se digitar algo nao esperado, mostra que a opcao eh invalida
            call limpatela
            setcursor 12, 12
            mov si, inva
            call printstring
            call delay
            call limpatela
            jmp loop..

    %endmacro

    ; macro para setar o cursor em algum lugar da tela baseado em duas posicoes x e y
    %macro setcursor 2

        mov ah, 0x2
        mov dh, %1
        mov dl, %2
        int 10h
    
    %endmacro

    ; macro para ler e transformar o valor digitado pelo usuario de string para int sem perder a posicao da sequencia
    %macro randt 0

        mov si, di
        mov di, resp
        call getstring

        mov di, si
        mov si, resp
        call stoi

    %endmacro

    ; macro para o comeco do jogo
    %macro comec 0
        pusha

        mov si, apagado
        mov di, 13
        call printimg
        setcursor 0, 13
        mov si, comeco
        call printstring
        setcursor 18, 6
        mov si, atencao
        call printstring
        call delay
        call delay
        call limpatela

        popa
    %endmacro

    ; macro para printar as opcoes de cores coloridas
    %macro printop 0
        setcursor 18, 0
        mov si, am
        mov bx, 1
        call printstringcor

        setcursor 20, 0
        mov si, az
        mov bx, 2
        call printstringcor

        setcursor 22, 0
        mov si, vd
        mov bx, 3
        call printstringcor

        setcursor 24, 0
        mov si, vm
        mov bx, 4
        call printstringcor

    %endmacro

    ;inicia o placar com 0
    %macro iniciaplac 0

        mov ax, 0
        mov di, placar
        call tostring

    %endmacro

    ; macro do placar
    %macro incrplacar 0
        pusha

        mov si, placar
        call stoi

        inc ax

        mov di, placar
        call tostring

        popa
    %endmacro

;|| MAIN ||
; comeco do programa
start:
    xor ax, ax
    mov ds, ax
    mov es, ax
    mov cx, ax
    mov dx, ax
    mov bx, ax

    ; chama o menu
    menu

    ; chama as funcoes e macros para iniciar o jogo
    .play:
        call limpatela
        comec
        iniciaplac
        call random
        mov di, sequencia
        mov cx, 1

    ; comeco do jogo
    .loop:
        cmp byte[di], 9
        je .fim
        mov di, sequencia
        mov dx, 1
        ; loop para acender as cores da sequencia
        .loop2:
            push cx
            push dx

            mov si, apagado
            call printimg
            call delay

            call limpatela

            pop dx
            pop cx

            cmp cx, dx
            jne .incr
            
            jmp .cont

        ; incremento para acender outras cores
        .incr:
            inc dx
            inc di
            jmp .loop2

        ; continuacao para a parte de acertar a cor
        .cont:
            mov dx, 1
            mov di, sequencia
            ; loop para perguntar a cor
            .loop3:
                ; guarda os valores dos contadores cx e dx para nao se perderem
                push cx
                push dx

                setcursor 0, 0
                mov si, digite
                call printstring

                printop

                setcursor 0, 28
                randt

                ; compara a resposta dada com o numero correto que temos na sequencia de cores
                cmp al, byte[di]
                ; se nao for igual, pula pro erro
                jne .erro

                ; se for continua

                ;resgata os valores de dx e cx
                pop dx
                pop cx
                ; se os dois contadores nao forem iguais, pula para incrementar dx
                cmp cx, dx
                jne .incr2
                
                ;quando os dois foram forem iguais incrementa o placar
                incrplacar
                ; se forem verifica se a sequencia de cores acabou
                push cx
                inc di
                call limpatela
                cmp byte[di], 9
                ; se acabou, pula para vitoria
                je .vit
                
                ; se nao, parabeniza o jogador e direciona para proxima rodada
                setcursor 12, 10
                mov si, cont
                call printstring
                setcursor 14, 7
                mov si, plac
                call printstring
                setcursor 14, 31
                mov si, placar
                call printstring
                call delay
                call delay
                call limpatela

                pop cx
                inc cx
                jmp .loop

            ; incrementa dx e di ate acabar a sequencia correspondente a rodada
            .incr2:
                inc dx
                inc di
                jmp .loop3

            ; caso o jogador tenha errado a sequencia, acaba o jogo
            .erro:
                call limpatela
                setcursor 12, 3
                mov si, erro
                call printstring
                call delay
                call delay
                ; pula para o menu de escolha
                jmp .esc

    ; caso a sequencia tenha acabado, o jogador venceu e eh parabenizado
    .vit:
        setcursor 12, 6
        mov si, vit
        call printstring
        setcursor 12, 31
        mov si, sorriso
        call printstring
        call delay
        call delay
        ; pula para o menu de escolha
        jmp .esc

    ; escolhe se vai jogar novamente, ir para o menu ou sair
    .esc:
        .loop5:
            call limpatela
            setcursor 17, 0
            mov si, esc
            call printstring

            setcursor 13, 9
            mov si, placfin
            call printstring
            setcursor 13, 30
            mov si, placar
            call printstring

            mov si, apagado
            call printimgap

            ; espera o jogador digitar a resposta
            mov ah, 00h
            int 16h

            cmp al, "1"
            je .play

            cmp al, "2"
            je start

            cmp al, "4"
            je .fim

            ; se digitar algo invalido, mostra na tela e repete o menu escolha
            call limpatela
            setcursor 12, 12
            mov si, inva
            call printstring
            call delay
            call limpatela
            jmp .loop5

    ; fim do jogo
    .fim:
        call limpatela
        setcursor 12, 6
        mov si, saida
        call printstring
        mov si, coracao
        setcursor 13, 27
        call printstring
        call delay
        call delay
        jmp $

    ; creditos do jogo
    .cred:
        call limpatela
        setcursor 0, 0
        mov si, credit
        call printstring

        setcursor 24, 0
        mov si, ent
        call printstring

        mov ah, 00h
        int 16h

        jmp start

jmp $